        // 테스트넷 컨트렉 

        _mint(msg.sender, 100000000 * 10 ** decimals());
        // _mint(0x769D674eDe614d79e79cE157B561B4F89Fac96d5, 150000000 * 10 ** decimals());
        // _mint(0xcB8E27169eC43e75751CEa7033e1E39aeD5595f3, 150000000 * 10 ** decimals());
        // _mint(0x643FF6fe36a18bF0d705fb89CEfC42deD01CF28d, 100000000 * 10 ** decimals());
        // _mint(0x7Cb592BBEE577867B735419402aF95F0605ecA1C, 150000000 * 10 ** decimals());
        // _mint(0xd4B11779a2dDAb1B49bcC873b87501f0C1319BFa, 250000000 * 10 ** decimals());
        // _mint(0xa90FF9C33e14C2d0badca9c137d46f33765613E2, 200000000 * 10 ** decimals());

        _confirmers.push(msg.sender);
        // _confirmers.push(confirmer1);
        // _confirmers.push(confirmer2);
        _resetConfirmed();
    }

    function pause() public onlyOwner isConfirmed 
    {
        _pause();
        _resetConfirmed();
    }

    function unpause() public onlyOwner isConfirmed 
    {
        _unpause();
        _resetConfirmed();
    }

    function transferOwnership(address newOwner) public isConfirmer(msg.sender) isConfirmed override
    {
        require(newOwner != address(0), "Ownable: new owner is the zero address");

        super._transferOwnership(newOwner);
        _resetConfirmed();
    }

    function renounceOwnership() public onlyOwner isConfirmed override
    {
        super.renounceOwnership();
        _resetConfirmed();
    }

    function setLock(address walletAddress, uint256 startTime, uint256 releaseMonths, uint256 amount) 
        public
        onlyOwner 
    {
        require(_lockInfos[walletAddress].amount < 1, "Aleady exist lock info.");
        require(block.timestamp < startTime, "ERC20: Current time is greater than start time.");
        require(releaseMonths > 0, "ERC20: ReleaseMonths is greater than 0.");
        require(amount > 0, "ERC20: Amount is greater than 0.");

        _lockInfos[walletAddress] = LockInfo(amount, startTime, releaseMonths);
        _lockedWallets.push(walletAddress);

        emit TimeLock( walletAddress, amount, startTime, releaseMonths ); 
    }

    function setLockReleaseMonths(address walletAddress, uint256 releaseMonths)
        public
        onlyOwner
        isConfirmed
    {
        require(_lockInfos[walletAddress].amount > 0, "Not exist lock info.");
        require(releaseMonths > 0, "ERC20: ReleaseMonths is greater than 0.");
        _lockInfos[walletAddress].releaseMonths = releaseMonths;

        emit TimeLockUpdate (walletAddress, 3, releaseMonths);
        _resetConfirmed();
    }

    function setLockStartTime(address walletAddress, uint256 startTime)
        public
        onlyOwner
        isConfirmed
    {
        require(_lockInfos[walletAddress].amount > 0, "Not exist lock info.");
        require(block.timestamp < startTime, "ERC20: Current time is greater than start time");
        _lockInfos[walletAddress].startTime = startTime;

        emit TimeLockUpdate (walletAddress, 2, startTime);
        _resetConfirmed();
    }

    function setLockAmount(address walletAddress, uint256 amount)
        public
        onlyOwner
        isConfirmed
    {
        require(_lockInfos[walletAddress].amount > 0, "Not exist lock info.");
        require(amount > 0, "ERC20: Amount is greater than 0.");
        _lockInfos[walletAddress].amount = amount;

        emit TimeLockUpdate (walletAddress, 1, amount);
        _resetConfirmed();
    }

    function getLockInfo(address walletAddress) 
        public 
        view 
        returns (uint256 lockAmount, uint256 startTime, uint256 releaseMonths, uint256 released) 
    {
        require(msg.sender == walletAddress || msg.sender == owner(), "Caller is not the owner.");
        require(_lockInfos[walletAddress].amount > 0, "Not exist lock info.");

        uint256 unLockAmount = _getUnLockAmount(walletAddress, block.timestamp);

        return (_lockInfos[walletAddress].amount, _lockInfos[walletAddress].startTime, _lockInfos[walletAddress].releaseMonths, unLockAmount);
    }

    function getLockWallets()
        public
        view
        virtual
        onlyOwner
        returns (address[] memory) 
    {
        return (_lockedWallets);
    }

    function _getUnLockAmount(address walletAddress, uint256 timestamp)
        internal
        view
        returns (uint256 unLockAmount)
    {
        uint256 lockAmount = _lockInfos[walletAddress].amount;
        uint256 lockStartTime = _lockInfos[walletAddress].startTime;
        uint256 releaseMonths = _lockInfos[walletAddress].releaseMonths;

        if (timestamp < lockStartTime) return 0;

        uint256 checkReleasedSecond = timestamp - lockStartTime;
        uint256 checkReleasedMonth = checkReleasedSecond / (86400 * 30);  //per 30day        

        if (releaseMonths <= checkReleasedMonth) {
            return lockAmount;
        } else if (checkReleasedMonth < 1) {
            return 0;
        } else {
            return lockAmount * checkReleasedMonth / releaseMonths;
        }
    }

    function _isLocked(address walletAddress, uint256 amount) 
        internal 
        view 
        returns (bool) 
    {
        if (_lockInfos[walletAddress].amount != 0) {
            uint256 unLockAmount = _getUnLockAmount(walletAddress, block.timestamp);
            return (balanceOf(walletAddress) - (_lockInfos[walletAddress].amount - unLockAmount)) < amount;
        } else {
            return false;
        }
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        require( !_isLocked( from, amount ) , "ERC20: Locked balance.");
        super._beforeTokenTransfer(from, to, amount);
    }

}

contracts/KlaytnKIP37Modify.sol.content:
/*
    SPDX-License-Identifier: MIT
    Create by. Jacob 2022. 07. 07
*/
pragma solidity ^0.8.7;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155URIStorageUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract KlaytnKIP37Modify is ERC1155URIStorageUpgradeable {
    address owner;

    mapping (uint256 => string) private _uris;
    mapping (uint256 => address) private creatorList;
    mapping (string => address) private marketOwnerList;

    /*
        디폴트로 넣어두는 TOKEN URI
    */
    function initialize() initializer public {
        __setOwner(msg.sender);
        __ERC1155_init("https://denaissance.mypinata.cloud/ipfs/{id}");
    }

    /*
        발행정보
    */
    event assetInformation (
        uint256 indexed tokenId,
        string tokenUri,
        uint256 quantity,
        address ownerAdress 
    );

    /*
        상장정보
    */
    event marketInformation (
        string indexed marektId,
        uint256 tokenId,
        string tokenUri,
        uint256 quantity,
        address ownerAdress 
    );

    /*
        구매정보
    */
    event orderInformation (
        string indexed marektId,
        uint256 tokenId,
        string tokenUri,
        uint256 quantity,
        address sellerAdress,
        address buyerAdress 
    );

    function __setOwner(address _owner) internal onlyInitializing {
        owner = _owner;
    }

    function getOwner() public view returns(address) {
        return owner;
    }

    /*
        Set ApprovalForAll
    */
    function setApprovalForAll(address operator, bool approved) public virtual override {
        require(msg.sender == owner, 'Only Owner!');

        _setApprovalForAll(owner, operator, approved);
    }

    /*
        Delete ApprovalForAll
    */
    function deleteApprovalForAll(address operator) public virtual {
        require(msg.sender == owner || isApprovedForAll(owner, operator), 'Only Owner!');

        _setApprovalForAll(owner, operator, false);
    }

    /*
        MINTING 전용 함수
        Creator Adress를 TokenId에 맵핑
    */
    function mint(address _creator, uint256 _tokenId, uint256 _quantity) public {
        require(owner == msg.sender, 'Only Owner!');

        _mint(_creator, _tokenId, _quantity, "");
        creatorList[_tokenId] = _creator;
    }

    /*
        발행하는 토큰의 토큰 URI를 덮어씌워 준다.
    */
    function uri(uint256 tokenId) override public view returns (string memory) {
        return (_uris[tokenId]);
    }

    /* 
        발행한 TOKEN URI를 TOKEN ID와 맵핑해서 저장 해준다.
    */ 
    function setTokenUri(uint256 _tokenId, string memory _uri) public {
        require(bytes(_uris[_tokenId]).length == 0, "Cannot set uri twice");

        _uris[_tokenId] = _uri; 
    }

    /* 
        발행 함수
    */ 
    function setMint(
        uint256 _tokenId,
        uint256 _supply,
        string memory _tokenUri,
        address _creator
    ) public returns(uint256) {
        require(owner == msg.sender, 'Only Owner!');
        require(_supply > 0, 'Check Supply!');
        require(_tokenId > 0, 'Check Token Id!');
        require(bytes(_tokenUri).length > 0, 'Check Token Uri!');

        /* 발행 */
        mint(_creator, _tokenId, _supply);
        setTokenUri(_tokenId, _tokenUri);

        /* Creator Adress를 TokenId에 맵핑 */
        creatorList[_tokenId] = _creator;

        /* 해당 거래를 블록에 기록해 준다. */
        emit assetInformation (
            _tokenId,
            _tokenUri,
            _supply,
            _creator
        );

        return _tokenId;
    }

    /* 
        상장할때 마켓에 Owner를 저장해준다.
    */ 
    function setListing(
        string memory _marketId,
        uint256 _tokenId,
        uint256 _supply,
        string memory _tokenUri,
        address _seller
    ) public returns(string memory) {
        require(owner == msg.sender, 'Only Owner!');
        require(bytes(_marketId).length > 0, 'Check Market Id!');
        require(_supply > 0, 'Check Supply!');
        require(_tokenId > 0, 'Check Token Id!');
        require(bytes(_tokenUri).length > 0, 'Check Token Uri!');

        /* Seller Adress를 MarketId에 맵핑 */
        marketOwnerList[_marketId] = _seller;

        /* Seller setApprove */
        setApprovalForAll(_seller, true);

        /* 해당 거래를 블록에 기록해 준다. */
        emit marketInformation (
            _marketId,
            _tokenId,
            _tokenUri,
            _supply,
            _seller
        );

        return _marketId;
    }

    /*
        구매 전용 함수     
    */
    function setOrder(
        string memory _marketId,
        uint256 _tokenId,
        uint256 _quantity,
        uint256 _createFee,
        uint256 _serviceFee,
        uint256 _sellerPrice,
        string memory _tokenUri,
        bool _isBalanceEmpty
    ) public payable returns (uint256) {
        // require(msg.sender == owner || isApprovedForAll(owner, marketOwnerList[_marketId]), 'Only Owner or Approved!');
        require(bytes(_marketId).length > 0, 'Check Market Id!');
        require(balanceOf(marketOwnerList[_marketId], _tokenId) >= _quantity, 'Check Quantity!');
        require(_tokenId > 0, 'Check Token Id!');
        require(bytes(_tokenUri).length > 0, 'Check Token Uri!');
        require(_createFee + _serviceFee + _sellerPrice  == msg.value, 'Check Price!');

        /*
            구매자에게 수량 만큼 asset을 전송
        */
        _safeTransferFrom(marketOwnerList[_marketId], msg.sender, _tokenId, _quantity, "0x00");

        /*
            Create Fee 분배
            0% 일때는 그냥 통과한다.
        */
        if (_createFee > 0) {
            payable(creatorList[_tokenId]).transfer(_createFee);
        }

        /*
            Service Fee -> 2.5% 분배
            Seller에게는 판매가
            (크리에이터 수수료 - 서비스 수수료) 계산 후 전송
        */
        payable(marketOwnerList[_marketId]).transfer(_sellerPrice);
        payable(owner).transfer(_serviceFee);

        /*
            가지고 있는 수량이 없으면 권한 제거
        */
        if (_isBalanceEmpty) {
            deleteApprovalForAll(marketOwnerList[_marketId]);
        }


        /* 해당 거래를 블록에 기록해 준다. */
        emit orderInformation (
            _marketId,
            _tokenId,
            _tokenUri,
            _quantity,
            marketOwnerList[_marketId],
            msg.sender
        );

        return _tokenId;
    }

    /*
        원화 전용 함수     
    */
    function setKRWOrder(
        string memory _marketId,
        uint256 _tokenId,
        string memory _tokenUri,
        uint256 _quantity,
        bool _isBalanceEmpty,
        address buyer
    ) public payable returns (uint256) {
        require(msg.sender == owner || isApprovedForAll(owner, marketOwnerList[_marketId]), 'Only Owner or Approved!');
        require(bytes(_marketId).length > 0, 'Check Market Id!');
        require(balanceOf(marketOwnerList[_marketId], _tokenId) >= _quantity, 'Check Quantity!');
        require(_tokenId > 0, 'Check Token Id!');
        require(bytes(_tokenUri).length > 0, 'Check Token Uri!');

        /*
            구매자에게 수량 만큼 asset을 전송
        */
        _safeTransferFrom(marketOwnerList[_marketId], buyer, _tokenId, _quantity, "0x00");

        /*
            가지고 있는 수량이 없으면 권한 제거
        */
        if (_isBalanceEmpty) {
            deleteApprovalForAll(marketOwnerList[_marketId]);
        }

        /* 해당 거래를 블록에 기록해 준다. */
        emit orderInformation (
            _marketId,
            _tokenId,
            _tokenUri,
            _quantity,
            marketOwnerList[_marketId],
            buyer
        );

        return _tokenId;
    }

    /*
        원화 전용 복구함수     
    */
    function setKRWOrderRestore(
        string memory _marketId,
        uint256 _tokenId,
        string memory _tokenUri,
        uint256 _quantity,
        bool _isBalanceEmpty,
        address buyer
    ) public payable returns (uint256) {
        require(msg.sender == owner, 'Only Owner!');
        require(bytes(_marketId).length > 0, 'Check Market Id!');
        require(balanceOf(marketOwnerList[_marketId], _tokenId) >= _quantity, 'Check Quantity!');
        require(_tokenId > 0, 'Check Token Id!');
        require(bytes(_tokenUri).length > 0, 'Check Token Uri!');

        /*
            구매자에게 수량 만큼 asset을 전송
        */
        _safeTransferFrom(marketOwnerList[_marketId], buyer, _tokenId, _quantity, "0x00");

        /*
            가지고 있는 수량이 없으면 권한 제거
        */
        if (_isBalanceEmpty) {
            deleteApprovalForAll(marketOwnerList[_marketId]);
        }

        /* 해당 거래를 블록에 기록해 준다. */
        emit orderInformation (
            _marketId,
            _tokenId,
            _tokenUri,
            _quantity,
            marketOwnerList[_marketId],
            buyer
        );

        return _tokenId;
    }
}

